const createWorker = require('./createWorker');

const recognize = async (image, langs, options) => {
  const worker = await createWorker(langs, 1, options);
  return worker.recognize(image)
    .finally(async () => {
      await worker.terminate();
    });
};

const detect = async (image, options) => {
  const worker = await createWorker('osd', 0, options);
  return worker.detect(image)
    .finally(async () => {
      await worker.terminate();
    });
};

module.exports = {
  recognize,
  detect,
};

import React from 'react';
import Tesseract from 'tesseract.js';

const extractTextFromImage = async (imageUri) => {
  try {
    const result = await Tesseract.recognize(
      imageUri,
      'eng', // Langue de reconnaissance
      { logger: (m) => console.log(m) } // Optionnel : journalisation des Ã©tapes
    );
    console.log(result.data.text); // Texte extrait de l'image
    return result.data.text;
  } catch (error) {
    console.error('Erreur lors de l\'extraction du texte:', error);
    return null;
  }
};

export default extractTextFromImage;
